// import footer from './components/Footer/index';


class switchBadges extends Component {
  state = {
    Badges
  }

  shuffleBadges = () => {
    var shuffledArr;
   shuffledArr =  Badges.shuffle(Badges)
  }

  // clickedBadge = (this.shuffleBadges) => {
