import Badges from "./Badges.json";
import footer from './components/Footer/index';
import { render } from 'react-dom';


class App extends Component {
  state = {
    Badges
  };

  clickedBadge = id => {
    const newArr = [];
    const Badges = this.state.Badges.forEach(event => Badges.id)
    newArr.push(Badges.id);
    for (let i = 0; i < Badges.length; i++) {
    if (newArr[i] == Badges.id)
    alert("Sorry you lost!")
    }
  }





render(){
  return (
    <React.Fragment>
      <Bootstrap />

      {console.log(this.state.Badges)}


      <row>

        <BadgeCard
          image={Badges[0].image} key={Badges[0].id}
        />
        <BadgeCard
          image={Badges[1].image} key={Badges[1].id}
        />
        <BadgeCard
          image={Badges[2].image} key={Badges[2].id}
        />
        <BadgeCard
          image={Badges[3].image} key={Badges[3].id}
        />
      </row>
      <row>
        <BadgeCard
          image={Badges[4].image} key={Badges[4].id}
        />
        <BadgeCard
          image={Badges[5].image} key={Badges[5].id}
        />
        <BadgeCard
          image={Badges[6].image} key={Badges[6].id}
        />
        <BadgeCard
          image={Badges[7].image} key={Badges[7].id}
        />
      </row>
      <footer />
    </React.Fragment>
  );
}
}

export default App;
